var expect=chai.expect;describe("Clase Medida",function(){describe("constructor",function(){it("Deberia tener un constructor con dos parámetros",function(){var e=new Medida(32,"f");expect(e.valor).to.equal(32)}),it("Deberia poder recibir un solo parametro",function(){var e=new Medida("5 f");expect(e.valor).to.equal(5),expect(e.tipo).to.equal("f")})}),describe("#convertir",function(){it("Deberia convertir de F a C",function(){expect(Medida.convertir("32f to c")).to.equal("0.00 Celsius")}),it("Deberia dar error al pasar a una temperatura desconocida",function(){expect(Medida.convertir("32f to f")).to.equal('Desconozco como convertir desde "f" hasta "f"')}),it("Deberia dar error al pedir una conversion erronea",function(){expect(Medida.convertir("32f a k")).to.equal("Introduzca una temperatura valida: 32.6e-8 F to C")})}),describe("#funcion match",function(){it("Debería aceptar patron",function(){var e=Medida.match("330e-1 f to c");expect(parseFloat(e.num)).to.equal(33),expect(e.temp1).to.equal("f"),expect(e.temp2).to.equal("c")})})}),describe("Clase Temperatura",function(){describe("constructor",function(){it("Deberia dar valor a los atributos (tipo, valor) - 2 argumentos ",function(){var e=new Temperatura(32,"c");expect(e.valor).to.equal(32),expect(e.tipo).to.equal("c")}),it("Deberia dar valor a los atributos (tipo, valor) - 1 argumento",function(){var e=new Temperatura("32 c");expect(e.valor).to.equal(32),expect(e.tipo).to.equal("c")})})}),describe("Clase Celsius",function(){describe("constructor celsius",function(){it("Deberia tener un constructor",function(){var e=new Celsius(37);expect(e.valor).to.equal(37)})}),describe("#toFarenheit",function(){it("Deberia pasar a Farenheit",function(){var e=new Celsius(32);expect(e.toFarenheit()).to.equal(89.6)})}),describe("#toKelvin",function(){it("Deberia pasar a Kelvin",function(){var e=new Celsius(32);expect(e.toKelvin()).to.equal(305.15)})})}),describe("Clase Farenheit",function(){describe("constructor farenheit",function(){it("Deberia tener un constructor",function(){var e=new Farenheit(32);expect(e.valor).to.equal(32),expect(e).to.be["instanceof"](Farenheit)})}),describe("#toCelsius",function(){it("Deberia pasar a Celsius",function(){var e=new Farenheit(32);expect(e.toCelsius()).to.equal(0)})}),describe("#toKelvin",function(){it("Deberia pasar a Kelvin",function(){var e=new Farenheit(32);expect(e.toKelvin()).to.equal(273.15)})})}),describe("Clase Kelvin",function(){describe("constructor farenheit",function(){it("Deberia tener un constructor",function(){var e=new Kelvin(32);expect(e.valor).to.equal(32),expect(e).to.be["instanceof"](Kelvin)})}),describe("#toFarenheit",function(){it("Deberia pasar a Farenheit",function(){var e=new Kelvin(273.15);expect(e.toFarenheit()).to.equal(32)})}),describe("#toCelsius",function(){it("Deberia pasar a Celsius",function(){var e=new Kelvin(273.15);expect(e.toCelsius()).to.equal(0)})})}),describe("Conversiones usando medida",function(){describe("Celsius",function(){var e=new Celsius(0);it("#toFarenheit",function(){expect(e.toFarenheit()).to.equal(32)}),it("#toKelvin",function(){expect(e.toKelvin()).to.equal(273.15)})}),describe("Kelvin",function(){var e=new Kelvin(273.15);it("#toFarenheit",function(){expect(e.toFarenheit()).to.equal(32)}),it("#toCelsius",function(){expect(e.toCelsius()).to.equal(0)})}),describe("Farenheit",function(){var e=new Farenheit(32);it("#toCelsius",function(){expect(e.toCelsius()).to.equal(0)}),it("#toKelvin",function(){expect(e.toKelvin()).to.equal(273.15)})})});